$(".label").click(function(){var e=$(this).text();$(".label").removeClass("active");$(this).addClass("active");if(e==="all")$("li").fadeIn();else{$("li").fadeOut();$("[data-category='"+e+"']").fadeIn("slow")}});$(document).ready(function(){$("a[href^='#']").click(function(e){e.preventDefault();var t=this.href,n=t.split("#"),r=n[1],i=$("#"+r).offset(),s=i.top;$("html, body").animate({scrollTop:s},1e3);window.location.hash="#"+r;$("#"+r).attr("tabindex",-1).focus()});window.location.hash&&$(window.location.hash).attr("tabindex",-1).focus()});